import React from 'react';

const AuthForm = ({ isSignup, onSubmit }) => {
  return (
    <form onSubmit={onSubmit} className="space-y-4">
      {isSignup && (
        <>
          <input type="text" placeholder="Name" className="w-full p-2 border rounded" required />
          <input type="text" placeholder="Qualification" className="w-full p-2 border rounded" required />
        </>
      )}
      <input type="email" placeholder="Email" className="w-full p-2 border rounded" required />
      <input type="text" placeholder="Phone Number" className="w-full p-2 border rounded" required />
      <button type="submit" className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">
        {isSignup ? "Sign Up" : "Login"}
      </button>
    </form>
  );
};

export default AuthForm;
